    },
  ];

  void _onItemTapped(int index) {
    setState(() {
      _selectedIndex = index;
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: PreferredSize(
        preferredSize: Size.fromHeight(80.0), // Adjust the height as needed
        child: AppBar(
          backgroundColor: Colors.red, // Background color of the app bar
          elevation: 0, // Remove the default app bar elevation
          shape: RoundedRectangleBorder(
            borderRadius: BorderRadius.only(
              bottomLeft: Radius.circular(20.0),
              bottomRight: Radius.circular(20.0),
            ),
          ),
          title: Text(
            'Emergency Contact List',
            style: TextStyle(color: Colors.white),
          ),
          leading: IconButton(
            icon: Icon(Icons.arrow_back, color: Colors.white,),
            onPressed: () {
              // Add functionality to navigate back
            },
          ),
          actions: [
            IconButton(
              icon: Icon(Icons.add, color: Colors.white,),
              onPressed: () {
                // Add functionality to add new contact
              },
            ),
          ],
        ),
      ),
      body: ListView.builder(
        itemCount: contacts.length,
        itemBuilder: (context, index) {
          return Padding(
            padding:
                const EdgeInsets.symmetric(vertical: 8.0, horizontal: 16.0),
            child: Card(
              elevation: 1, // Adjust the elevation as needed
              shadowColor: Colors.black, // Shadow color
              shape: RoundedRectangleBorder(
                borderRadius: BorderRadius.circular(15.0),
                side: BorderSide(color: Colors.black), // Border color
              ),
              child: ListTile(
                contentPadding: EdgeInsets.all(16.0),
                leading: CircleAvatar(
                  backgroundImage:
                      NetworkImage(contacts[index]['profilePicture']),
                ),
                title: Text(contacts[index]['name']),
                subtitle: Text(contacts[index]['contactNumber']),
                onTap: () async {
                  String contactNumber =
                      'tel:${contacts[index]['contactNumber']}';
                  if (await canLaunch(contactNumber)) {
                    await launch(contactNumber);
                  } else {
                    throw 'Could not launch $contactNumber';
                  }
                },
                trailing: Icon(Icons.more_horiz),
              ),
            ),
          );
        },
      ),
      bottomNavigationBar: BottomNavigationBar(
        currentIndex: _selectedIndex,
        onTap: _onItemTapped,
        type: BottomNavigationBarType.fixed,
        selectedItemColor: Colors.red,
        items: [
          BottomNavigationBarItem(
            icon: Icon(Icons.home,color: Colors.black,),
            label: 'Home',
          ),
          BottomNavigationBarItem(
            icon: Icon(Icons.search,color: Colors.black,),
            label: 'Folder',
          ),
          BottomNavigationBarItem(
            icon: Icon(Icons.notifications,color: Colors.black,),
            label: 'Star',
          ),
          BottomNavigationBarItem(
            icon: Icon(Icons.person,color: Colors.black,),
            label: 'File',
          ),
          BottomNavigationBarItem(
            icon: Icon(Icons.person, color: Colors.black),
            label: 'Contacts',
          ),
        ],
